import base64, struct
bmp_data = base64.b64decode('')


def bmp_info(data):
    data_byte = struct.unpack('<ccIIIIIIHH', data[:30])
    print(data_byte)
    return {
        'width': data_byte[6],
        'height': data_byte[7],
        'color': data_byte[9]
    }


# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')
